import React from "react";

//Local stuff.
import "../styles/main.scss";
import HeadShot from "../assets/head_shot.png";
import Navigation from "../components/Navigation";
import "../styles/bounce.css";

class Home extends React.Component {
  bounce() {
    var element = document.getElementById("hsc");
    element.classList.add("animation-target");
  }

  render() {
    return (
      <div className="home">
        <div className="center-page">
          <div className="main-width">
            <Navigation />

            <div className="flex-content-a-container">
              <div className="flex-content-a">
                <div onPointerDown={this.bounce}>
                  <h1 className="header-text black-text heavy-font">
                    Hi, I'm<br></br>Alexander<br></br>Stewart
                    <span className="period">.</span>
                  </h1>
                </div>

                <div id="hsc" className="head-shot-container">
                  <img
                    className="head-shot"
                    src={HeadShot}
                    alt="Alexander Stewart head shot"
                  />
                </div>
              </div>
            </div>

            <p className="blurb black-text light-font">
              I'm a front end web developer living in Toronto. I enjoy
              developing and designing websites and I dabble in backend
              development. Feel free to contact me -
              alexander.stewart.dev@gmail.com.
            </p>
            <div className="space-a"></div>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
