import React from "react";

//libraries.
import { Link } from "react-router-dom";
import ParticlesBg from "particles-bg";

//Local stuff.
import "../styles/main.scss";
import HeadShot from "../assets/head_shot.png";

function Home() {
  let config = {
    num: [4, 7],
    rps: 0.1,
    radius: [5, 40],
    life: [1.5, 3],
    v: [2, 3],
    tha: [-40, 40],
    // body: "./img/icon.png", // Whether to render pictures
    // rotate: [0, 20],
    alpha: [0.6, 0],
    scale: [1, 0.1],
    position: "center", // all or center or {x:1,y:1,width:100,height:100}
    color: ["random", "#ff0000"],
    cross: "dead", // cross or bround
    random: 15, // or null,
    g: 5, // gravity
    // f: [2, -1], // force
    onParticleUpdate: (ctx, particle) => {
      ctx.beginPath();
      ctx.rect(
        particle.p.x,
        particle.p.y,
        particle.radius * 2,
        particle.radius * 2
      );
      ctx.fillStyle = particle.color;
      ctx.fill();
      ctx.closePath();
    },
  };

  return (
    <div className="home">
      <div className="center-page">
        <div className="main-width">
          <div className="navigation">
            <Link className="link" to="/">
              <p className="link-about black-text light-font">About</p>
            </Link>
            <Link className="link" to="/">
              <p className="link-portfolio black-text light-font">Portfolio</p>
            </Link>
          </div>

          <div className="flex-content-a-container">
            <div className="flex-content-a">
              <h1 className="header-text black-text heavy-font">
                Hi, I'm<br></br>Alexander<br></br>Stewart
                <span style={{ color: "#FF9D9D" }}>.</span>
              </h1>

              <div className="head-shot-container">
                <img
                  className="head-shot"
                  src={HeadShot}
                  alt="Alexander Stewart head shot"
                />
              </div>
            </div>
          </div>

          <p className="blurb black-text light-font">
            I'm a front end web developer living in Toronto. I enjoy developing
            and designing websites and I dabble in backend development. Feel
            free to contact me - alexander.stewart.dev@gmail.com.
          </p>
          <div className="space-a"></div>
        </div>
      </div>
      <ParticlesBg type="custom" config={config} bg={true} />
    </div>
  );
}

export default Home;
