import React from "react";

//Local stuff.
import "../styles/main.scss";
import HeadShot from "../assets/head_shot.png";
import NavigationAbout from "../components/NavigationAbout";
import { useRef, useLayoutEffect } from "react";

function Home() {
  const lineB = useRef(0);

  useLayoutEffect(() => {
    lineB.current.style.backgroundColor = "green";
  });

  const cursorDot = useRef();
  const cursorDotOutline = useRef();

  //box.current.backgroundColor = "blue";
  var cursor = {
    delay: 8,
    _x: 0,
    _y: 0,
    endX: window.innerWidth / 2,
    endY: window.innerHeight / 2,
    cursorVisible: true,
    cursorEnlarged: false,
    $dot: cursorDot,
    $outline: cursorDotOutline,

    init: function () {
      // Set up element sizes
      this.dotSize = this.$dot.current.offsetWidth;
      this.outlineSize = this.$outline.current.offsetWidth;

      this.setupEventListeners();
      this.animateDotOutline();
    },

    //     updateCursor: function(e) {
    //         var self = this;

    //         console.log(e)

    //         // Show the cursor
    //         self.cursorVisible = true;
    //         self.toggleCursorVisibility();

    //         // Position the dot
    //         self.endX = e.pageX;
    //         self.endY = e.pageY;
    //         self.$dot.style.top = self.endY + 'px';
    //         self.$dot.style.left = self.endX + 'px';
    //     },

    setupEventListeners: function () {
      var self = this;

      // Anchor hovering
      document.querySelectorAll("a").forEach(function (el) {
        el.addEventListener("mouseover", function () {
          self.cursorEnlarged = true;
          self.toggleCursorSize();
        });
        el.addEventListener("mouseout", function () {
          self.cursorEnlarged = false;
          self.toggleCursorSize();
        });
      });

      // Click events
      document.addEventListener("mousedown", function () {
        self.cursorEnlarged = true;
        self.toggleCursorSize();
      });
      document.addEventListener("mouseup", function () {
        self.cursorEnlarged = false;
        self.toggleCursorSize();
      });

      document.addEventListener("mousemove", function (e) {
        // Show the cursor
        self.cursorVisible = true;
        self.toggleCursorVisibility();

        // Position the dot
        self.endX = e.pageX;
        self.endY = e.pageY;
        self.$dot.current.top = self.endY + "px";
        self.$dot.current.left = self.endX + "px";
      });

      // Hide/show cursor
      document.addEventListener("mouseenter", function (e) {
        self.cursorVisible = true;
        self.toggleCursorVisibility();
        self.$dot.current.opacity = 1;
        self.$outline.current.opacity = 1;
      });

      document.addEventListener("mouseleave", function (e) {
        self.cursorVisible = true;
        self.toggleCursorVisibility();
        self.$dot.current.opacity = 0;
        self.$outline.current.opacity = 0;
      });
    },

    animateDotOutline: function () {
      var self = this;

      self._x += (self.endX - self._x) / self.delay;
      self._y += (self.endY - self._y) / self.delay;
      self.$outline.current.top = self._y + "px";
      self.$outline.current.left = self._x + "px";

      requestAnimationFrame(this.animateDotOutline.bind(self));
    },

    toggleCursorSize: function () {
      var self = this;

      if (self.cursorEnlarged) {
        self.$dot.current.transform = "translate(-50%, -50%) scale(0.75)";
        self.$outline.current.transform = "translate(-50%, -50%) scale(1.5)";
      } else {
        self.$dot.current.transform = "translate(-50%, -50%) scale(1)";
        self.$outline.current.transform = "translate(-50%, -50%) scale(1)";
      }
    },

    toggleCursorVisibility: function () {
      var self = this;

      if (self.cursorVisible) {
        self.$dot.current.opacity = 1;
        self.$outline.current.opacity = 1;
      } else {
        self.$dot.current.opacity = 0;
        self.$outline.current.opacity = 0;
      }
    },
  };

  cursor.init();

  return (
    <div className="home 1.1">
      <div className="cursor-dot-outline" ref={cursorDotOutline}></div>
      <div className="cursor-dot" ref={cursorDot}></div>
      <div className="center-page">
        <div className="main-width">
          <div className="line-a"></div>
          <div className="space-c"></div>
          <NavigationAbout />
          <div className="flex-content-a-container">
            <div className="flex-content-a">
              <h1 className="header-text black-text heavy-font">
                Hi, I'm<br></br>Alexander<br></br>Stewart
                <span className="period">.</span>
              </h1>

              <div className="head-shot-container">
                <img
                  id="hsc"
                  className="head-shot"
                  src={HeadShot}
                  alt="Alexander Stewart head shot"
                />
              </div>
            </div>
          </div>

          <p className="blurb black-text light-font">
            I'm a front end web developer living in Toronto. I currently attend
            Algonquin College for computer programming.
          </p>

          <p className="blurb black-text light-font text-align-right">
            <a href="https://github.com/AlexanderStewart">github</a>
            <br></br>
            <a href="https://www.linkedin.com/in/alexander-stewart-aaa048156/">
              linkedin
            </a>
          </p>
          <div className="space-c"></div>
          <div className="line-b" ref={lineB}></div>
        </div>
      </div>
    </div>
  );
}

export default Home;
