import React from "react";
import { useRef, useLayoutEffect } from "react";

//Local stuff.
import "../styles/main.scss";
import GoldenSum from "../assets/golden_sum.png";
import Circles from "../assets/circles.png";
import CompSciCalc from "../assets/comp_sci_calc.png";
import NavigationPortfolio from "../components/NavigationPortfolio";

function Home() {
  const cursorDot = useRef();
  const cursorDotOutline = useRef();

  useLayoutEffect(() => {
    var cursor = {
      delay: 8,
      _x: 0,
      _y: 0,
      endX: window.innerWidth / 2,
      endY: window.innerHeight / 2,
      cursorVisible: true,
      cursorEnlarged: false,
      $dot: cursorDot,
      $outline: cursorDotOutline,

      init: function () {
        // Set up element sizes
        this.dotSize = this.$dot.current.style.offsetWidth;
        this.outlineSize = this.$outline.current.style.offsetWidth;

        this.setupEventListeners();
        this.animateDotOutline();
      },

      //     updateCursor: function(e) {
      //         var self = this;

      //         console.log(e)

      //         // Show the cursor
      //         self.cursorVisible = true;
      //         self.toggleCursorVisibility();

      //         // Position the dot
      //         self.endX = e.pageX;
      //         self.endY = e.pageY;
      //         self.$dot.style.top = self.endY + 'px';
      //         self.$dot.style.left = self.endX + 'px';
      //     },

      setupEventListeners: function () {
        var self = this;

        // Anchor hovering
        document.querySelectorAll("a").forEach(function (el) {
          el.addEventListener("mouseover", function () {
            self.cursorEnlarged = true;
            self.toggleCursorSize();
          });
          el.addEventListener("mouseout", function () {
            self.cursorEnlarged = false;
            self.toggleCursorSize();
          });
        });

        // Click events
        document.addEventListener("mousedown", function () {
          self.cursorEnlarged = true;
          self.toggleCursorSize();
        });
        document.addEventListener("mouseup", function () {
          self.cursorEnlarged = false;
          self.toggleCursorSize();
        });

        document.addEventListener("mousemove", function (e) {
          // Show the cursor
          self.cursorVisible = true;
          self.toggleCursorVisibility();

          // Position the dot
          self.endX = e.pageX;
          self.endY = e.pageY;
          self.$dot.current.style.top = self.endY + "px";
          self.$dot.current.style.left = self.endX + "px";
        });

        // Hide/show cursor
        document.addEventListener("mouseenter", function (e) {
          self.cursorVisible = true;
          self.toggleCursorVisibility();
          self.$dot.current.style.opacity = 1;
          self.$outline.current.style.opacity = 1;
        });

        document.addEventListener("mouseleave", function (e) {
          self.cursorVisible = true;
          self.toggleCursorVisibility();
          self.$dot.current.style.opacity = 0;
          self.$outline.current.style.opacity = 0;
        });
      },

      animateDotOutline: function () {
        var self = this;

        self._x += (self.endX - self._x) / self.delay;
        self._y += (self.endY - self._y) / self.delay;
        self.$outline.current.style.top = self._y + "px";
        self.$outline.current.style.left = self._x + "px";

        requestAnimationFrame(this.animateDotOutline.bind(self));
      },

      toggleCursorSize: function () {
        var self = this;

        if (self.cursorEnlarged) {
          self.$dot.current.style.transform =
            "translate(-50%, -50%) scale(0.75)";
          self.$outline.current.style.transform =
            "translate(-50%, -50%) scale(1.5)";
        } else {
          self.$dot.current.style.transform = "translate(-50%, -50%) scale(1)";
          self.$outline.current.style.transform =
            "translate(-50%, -50%) scale(1)";
        }
      },

      toggleCursorVisibility: function () {
        var self = this;

        if (self.cursorVisible) {
          self.$dot.current.style.opacity = 1;
          self.$outline.current.style.opacity = 1;
        } else {
          self.$dot.current.style.opacity = 0;
          self.$outline.current.style.opacity = 0;
        }
      },
    };

    cursor.init();
  });

  return (
    <div className="portfolio">
      <div className="cursor-dot-outline" ref={cursorDotOutline}></div>
      <div className="cursor-dot" ref={cursorDot}></div>
      <div className="horizontally-center-page">
        <div className="main-width">
          <NavigationPortfolio />

          <div className="center">
            <h1 className="header-text-b black-text heavy-font">
              Here are<br></br>my<br></br>passion<br></br>projects
              <span className="period">.</span>
            </h1>
          </div>

          <div className="center">
            <div className="flex-content-a-container">
              <div className="flex-content-a">
                <div className="portfolio-content">
                  <img
                    className="portfolio-content-image"
                    src={GoldenSum}
                    alt="Golden Sum Screenshot"
                  />
                  <h2 className="heavy-font">
                    <a
                      className="red-text"
                      href="https://apps.apple.com/us/app/golden-sum-a-math-puzzle-game/id1458212055"
                    >
                      Golden Sum
                    </a>
                  </h2>
                  <p className="blurb blurb-b black-text light-font">
                    Golden sum is a number puzzle game for Android and IOS. It
                    was built with the UI toolkit Flutter and features in-app
                    purchases as well as ads.
                  </p>
                </div>
                <div className="space-b"></div>
                <div className="portfolio-content">
                  <img
                    className="portfolio-content-image"
                    src={Circles}
                    alt="Circles Screenshot"
                  />
                  <h2 className="heavy-font">
                    <a
                      className="red-text"
                      href="https://alexanderstewart.github.io/circles-web/"
                    >
                      Circles
                    </a>
                  </h2>
                  <p className="blurb blurb-b black-text light-font">
                    Circles is a number puzzle game made for the web that was
                    built with the framework React. Circles (as you can probably
                    tell) is heavily inspired by Golden Sum.
                  </p>
                </div>
                <div className="space-b"></div>
                <div className="portfolio-content">
                  <img
                    className="portfolio-content-image"
                    src={CompSciCalc}
                    alt="Circles Screenshot"
                  />
                  <h2 className="heavy-font">
                    <a
                      className="red-text"
                      href="https://alexanderstewart.github.io/comp-sci-calculator/#/"
                    >
                      CompSciCalculator
                    </a>
                  </h2>
                  <p className="blurb blurb-b black-text light-font">
                    CompSciCalculator is a really simple tool to convert between
                    number systems.
                  </p>
                </div>
              </div>
            </div>
          </div>

          <div className="space-a"></div>
        </div>
      </div>
    </div>
  );
}

export default Home;
